import React from "react";

export const LandingPreview = ({ landing }) => {
    console.log(landing)

    const date = new Intl.DateTimeFormat("en-US").format(landing.date)
    let desc = landing.details
    let imgLink = landing.links.patch.small
    desc = (desc === undefined) ? 'No details about this flight' : desc.substring(0, 20)
    return <div className="landing-preview">
        <h1>{landing.name}</h1>
        <span>{date}</span>
        <img src={imgLink} alt={`${landing.name} img`} />

        {desc && <p>{desc}...</p>}

    </div>
}
